  "golfD2": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Day 2- Baku Panoramic Tour – Absheron Peninsula- Dreamland Golf Club"])};fn.source="Day 2- Baku Panoramic Tour – Absheron Peninsula- Dreamland Golf Club";return fn;})(),
  "golfP2": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Visit Martyr's Alley, located on the higher slopes of the city's west end. This is the best place for panoramic views of the city and Baku Bay. Afterwards at midday, enjoy a half-day trip to Absheron Peninsula (30 km from the city center), visit the Ateshgah Zoroastrian fire temple and Yanar Dag. After that, we will go to Dreamland Golf Club. Leisure time. Back in town in the evening."])};fn.source="Visit Martyr's Alley, located on the higher slopes of the city's west end. This is the best place for panoramic views of the city and Baku Bay. Afterwards at midday, enjoy a half-day trip to Absheron Peninsula (30 km from the city center), visit the Ateshgah Zoroastrian fire temple and Yanar Dag. After that, we will go to Dreamland Golf Club. Leisure time. Back in town in the evening.";return fn;})(),
