  "restP1": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Through exceptional service, attention to detail, and a commitment to quality, we strive to create memorable dining experiences that leave a lasting impression on our guests, fostering an appreciation for Azerbaijani gastronomy and culture."])};fn.source="Through exceptional service, attention to detail, and a commitment to quality, we strive to create memorable dining experiences that leave a lasting impression on our guests, fostering an appreciation for Azerbaijani gastronomy and culture.";return fn;})(),
