  "Az6Days5Nights": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Azerbaiyán, 6 días y 5 noches"])};fn.source="Azerbaiyán, 6 días y 5 noches";return fn;})(),
  "Az6DaysD1": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Día 1: Llegada a Bakú"])};fn.source="Día 1: Llegada a Bakú";return fn;})(),
