  "bakuAndOutH4": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Architectural tour (4 hours)"])};fn.source="Architectural tour (4 hours)";return fn;})(),
  "bakuAndOutP4": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Discover the architectural wonders of Baku on our curated tour. From the historic elegance of the Palace of Happiness, Ismailiyya and Zeynalabdin Taghiyev's Palace to the modern marvels of Flame Towers and the Heydar Aliyev Center, immerse yourself in the captivating blend of tradition and innovation that defines Azerbaijan's capital. Continue your exploration of history as we introduce you to the magnificent Azerbaijan State Academic Philharmonic Hall, enriching your journey with cultural richness and inspiring architecture."])};fn.source="Discover the architectural wonders of Baku on our curated tour. From the historic elegance of the Palace of Happiness, Ismailiyya and Zeynalabdin Taghiyev's Palace to the modern marvels of Flame Towers and the Heydar Aliyev Center, immerse yourself in the captivating blend of tradition and innovation that defines Azerbaijan's capital. Continue your exploration of history as we introduce you to the magnificent Azerbaijan State Academic Philharmonic Hall, enriching your journey with cultural richness and inspiring architecture.";return fn;})(),
