  "carpetD9": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Dia 9: Excursão artística em Baku"])};fn.source="Dia 9: Excursão artística em Baku";return fn;})(),
  "carpetP9": (()=>{const fn=(ctx) => {const { normalize: _normalize } = ctx;return _normalize(["Hoje visitaremos o Museu Nacional de Arte do Azerbaijão. O museu é o maior museu de arte do Azerbaijão. Explore a herança artística do Azerbaijão neste grande museu. A coleção do museu inclui mais de 17 mil obras de arte, com mais de 3 mil itens em exposição permanente em 60 salas. A coleção inclui arte do Azerbaijão, bem como arte da Turquia, Irã, França, Japão, China e Ásia Central. Visite o Espaço de Arte Contemporânea Yarat. O local apresenta exposições temporárias de importantes artistas internacionais, destacando movimentos emergentes e novas encomendas. Alojamento em Baku."])};fn.source="Hoje visitaremos o Museu Nacional de Arte do Azerbaijão. O museu é o maior museu de arte do Azerbaijão. Explore a herança artística do Azerbaijão neste grande museu. A coleção do museu inclui mais de 17 mil obras de arte, com mais de 3 mil itens em exposição permanente em 60 salas. A coleção inclui arte do Azerbaijão, bem como arte da Turquia, Irã, França, Japão, China e Ásia Central. Visite o Espaço de Arte Contemporânea Yarat. O local apresenta exposições temporárias de importantes artistas internacionais, destacando movimentos emergentes e novas encomendas. Alojamento em Baku.";return fn;})(),
